Programa:
%MANILLA CORREDERAS
%-------------------
SI [OPCION("Activa","Sí")] ENTONCES
    SI [OPCION("Corredera","Izquierda") O OPCION ("Corredera","Derecha")] ENTONCES
        SI [OPCION("Oscilobatiente","Ninguna")=1)] ENTONCES
            %MANILLA INTERIOR:
            SI [OPCION("RO_COR_MANILLA INTERIOR","Cor. Tirador corto")] ENTONCES
                MENSAJE("Tirador corto corredera Roto interior");
                PIEZAC("RO_613226",[1]);
            FINSI
            SI [OPCION("RO_COR_MANILLA INTERIOR","Cor. Tirador largo")] ENTONCES
                MENSAJE("Tirador largo corredera Roto");
                PIEZAC("RO_613234",[1]);
            FINSI
            SI [OPCION("RO_COR_MANILLA INTERIOR","Cor. Tirador bombillo")] ENTONCES
                MENSAJE("Tirador bombillo corredera Roto interior");
                PIEZAC("RO_613243",[1]);
            FINSI
            SI [OPCION("RO_COR_MANILLA INTERIOR","Cor. Rotoline")] ENTONCES
                %cuadr. 30 mm
                MENSAJE("Manilla Rotoline interior");
                PIEZAC("RO_228176",[1]);
            FINSI
            SI [OPCION("RO_COR_MANILLA INTERIOR","Cor. Rotoline secustik")] ENTONCES
                %Adaptar el cuadr. 37mm
                MENSAJE("Manilla Rotoline Secustik interior");
                PIEZAC("RO_487657",[1]);
            FINSI
            SI [OPCION("RO_COR_MANILLA INTERIOR","Cor. Rotoline llave")] ENTONCES
                %Adaptar el cuadr. 35mm
                MENSAJE("Manilla Rotoline llave interior");
                PIEZAC("RO_228044",[1]);
            FINSI
            SI [OPCION("RO_COR_MANILLA INTERIOR","Cor. Rotoswing")] ENTONCES
                %Adaptar el cuadr. 37mm
                MENSAJE("Manilla Rotoswing interior");
                PIEZAC("RO_602370",[1]);
            FINSI
            SI [OPCION("RO_COR_MANILLA INTERIOR","Cor. Rotoswing secustik")] ENTONCES
                %Adaptar el cuadr. 37mm
                MENSAJE("Manilla Rotoswing secustik interior");
                PIEZAC("RO_602389",[1]);
            FINSI
            SI [OPCION("RO_COR_MANILLA INTERIOR","Cor. Rotoswing llave")] ENTONCES
                %Adaptar el cuadr. 37mm
                MENSAJE("Manilla rotoswing llave interior");
                PIEZAC("RO_602375",[1]);
            FINSI
            SI [OPCION("RO_COR_MANILLA INTERIOR","Cor. Manilla uñero")] ENTONCES
                MENSAJE("Uñero corredera Roto interior");
                PIEZAC("RO_312766",[1]);
            FINSI
            SI [OPCION("RO_COR_MANILLA INTERIOR","Cor. Manilla Acodada Izqda")]ENTONCES
                MENSAJE("Manilla Acodada Izqda Roto Interior");
                PIEZAC("RO_375816",[1]);
            FINSI
            SI [OPCION("RO_COR_MANILLA INTERIOR","Cor. Manilla Acodada Dcha")]ENTONCES
                MENSAJE("Manilla Acodada Dcha Roto Interior");
                PIEZAC("RO_375320",[1]);
            FINSI
            SI [OPCION("RO_COR_MANILLA INTERIOR","Cor. Manilla Quita y Pon")]ENTONCES
                MENSAJE("Manilla Quita y Pon Interior");
                PIEZAC("RO_228014",[1]);
                PIEZAC("RO_257628",[1]);
            FINSI


            %MANILLA EXTERIOR:
            SI [OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. uñero")] ENTONCES
                MENSAJE("Tirador uñero corredera Roto exterior");
                PIEZAC("RO_605543",[1]);
            FINSI
            SI [OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. corto")] ENTONCES
                MENSAJE("Tirador corto corredera Roto exterior");
                PIEZAC("RO_613226",[1]);
            FINSI
            SI [OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. largo")] ENTONCES
                MENSAJE("Tirador largo corredera Roto exterior");
                PIEZAC("RO_613234",[1]);
            FINSI
            SI [OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. bombillo")] ENTONCES
                MENSAJE("Tirador bombillo corredera Roto exterior");
                PIEZAC("RO_613217",[1]);
            FINSI
    
            %ACCESORIOS
            SI [OPCION("RO_COR_MANILLA EXTERIOR","Cor. Manilla ext. Ninguna")=0] ENTONCES
                %Definir tabla por tipos.
                SI [OPCION("RO_COR_MANILLA INTERIOR","Cor. Tirador corto") O 
                    OPCION("RO_COR_MANILLA INTERIOR","Cor. Tirador largo")] ENTONCES
                    SI [OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. uñero")] ENTONCES
                        %Tipo 1
                        PIEZA("RO_613173",[1]);
                    FINSI    
                    SI [OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. corto") O 
                        OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. largo")] ENTONCES
                        %Tipo 2
                        PIEZA("RO_613176",[1]);
                    FINSI
                    SI [OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. bombillo")] ENTONCES
                        ERROR("No es una combinación posible para manillas correderas");
                    FINSI
                FINSI
                SI [OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. bombillo")] ENTONCES
                    SI [OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. uñero")] ENTONCES
                        %Tipo 1
                        PIEZA("RO_613173",[1]);
                    FINSI    
                    SI [OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. corto") O 
                        OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. largo")] ENTONCES
                        %Tipo 2
                        PIEZA("RO_613176",[1]);
                    FINSI
                    SI [OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. bombillo")] ENTONCES
                         %Tipo 3
                        PIEZA("RO_613180",[1]);
                    FINSI
                FINSI
            FINSI
        FINSI
    FINSI
FINSI
