% ESTABLECE OPCIONES VISIBLES POR DEFECTO %


% 1. BASICAS CLIENTE

% Manilla %
SI[OPCION("RO_MANILLA BALCONERA","Oculto")]ENTONCES
    ESTABLECEOPCION("RO_MANILLA BALCONERA","Manilla + Manilla");
FINSI
% Tipo cerradero %
SI[OPCION("RO_NT_AL_TIPO CERRADERO","Oculto")]ENTONCES
    ESTABLECEOPCION("RO_NT_AL_TIPO CERRADERO","Con apriete");
FINSI
% Clic Retención %
SI[OPCION("RO_NX_CLIC RETENCION","Oculto")]ENTONCES
    ESTABLECEOPCION("RO_NX_CLIC RETENCION","Clic retencion_si");
FINSI
% Ventilacion reducida %
SI[OPCION("RO_VENTIL. REDUCIDA","Oculto")]ENTONCES                                  
    ESTABLECEOPCION("RO_VENTIL. REDUCIDA","VR_si");
FINSI

SI[OPCION("RO_NX_SOPORTE COMPAS P","Oculto")]ENTONCES
   ESTABLECEOPCION("RO_NX_SOPORTE COMPAS P","3/130");
FINSI


% 2. RESTO

% Control Mecanizados %
SI [OPCION("RO_0MEC","Oculto")] ENTONCES
    ESTABLECEOPCION("RO_0MEC","Por Opcion");
FINSI
% Aguja %
SI[OPCION("RO_AGUJA","Ag25")=0 Y OPCION("RO_AGUJA","Ag30")=0 Y OPCION("RO_AGUJA","Ag35")=0]ENTONCES
    ESTABLECEOPCION("RO_AGUJA","Ag30");
FINSI
% Easy Mix = Balconera %
SI[OPCION("RO_NX_EASY MIX","Oculto")]ENTONCES                                  
    ESTABLECEOPCION("RO_NX_EASY MIX","Easy Mix_Si");                                       
FINSI
% Bombillo %
SI[OPCION("RO_BOMBILLO","Oculto")]ENTONCES
    ESTABLECEOPCION("RO_BOMBILLO","b_sin bombillo");
FINSI
% Tramo Inferior Marco %
SI[OPCION("RO_TRAMO INFERIOR MARCO","Oculto")]ENTONCES
    ESTABLECEOPCION("RO_TRAMO INFERIOR MARCO","M_Con marco");
FINSI
% Tipo Herraje %
SI[OPCION("RO_NX_TIPO HERRAJE","Oculto")]ENTONCES
   ESTABLECEOPCION("RO_NX_TIPO HERRAJE","Normal");
FINSI
% Peso Designo %
SI[OPCION("RO_NX_TIPO HERRAJE","Designo") Y OPCION("RO_PESO_DESIGNO","Oculto")]ENTONCES
   ESTABLECEOPCION("RO_PESO_DESIGNO","Hasta 100kg (sin dispositivo)");              
FINSI
SI[OPCION("RO_NX_TIPO HERRAJE","Designo")=0]ENTONCES
   ESTABLECEOPCION("RO_PESO_DESIGNO","Oculto");
FINSI
% Bisagras practicable %
SI[OPCION ("RO_PU_AL_BISAGRA","Oculto")]ENTONCES
    ESTABLECEOPCION("RO_PU_AL_BISAGRA","SCR100 (100Kg)");
FINSI

% Herraje Activa %
SI [OPCION("Activa","Sí")]ENTONCES 
    %1H
    SI [OPCION("Asociada","Ninguna")]ENTONCES   
        ESTABLECEOPCION("RO_NX_HERR. HOJA PASIVA","Oculto");  
        ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","Oculto");                          
    FINSI
    %2H
    SI [OPCION("Asociada","Practicable")]ENTONCES
        SI [OPCION("RO_NT_AL_HOJA PASIVA","Oculto")]ENTONCES   
            ESTABLECEOPCION("RO_NT_AL_HOJA PASIVA","AL_Cremona");                    
        FINSI
        
    FINSI 
FINSI


% 3. MECANIZADOS

% Mecanizado Manilla %
SI [OPCION("RO_0MEC","Por Opcion")] ENTONCES
    SI[OPCION("RO_MANILLA BALCONERA","Solo manilla interior")]ENTONCES
        ESTABLECEOPCION("RO_NT_MANILLA TRIPLE","mt_interior");
    FINSI
    SI[OPCION("RO_MANILLA BALCONERA","Manilla balconera Ninguna")]ENTONCES
        ESTABLECEOPCION("RO_NT_MANILLA TRIPLE","mt_sin manilla triple");
    FINSI
    SI[OPCION("RO_MANILLA BALCONERA","Solo manilla interior")=0 Y OPCION("RO_MANILLA BALCONERA","Manilla balconera Ninguna")=0] ENTONCES
        ESTABLECEOPCION("RO_NT_MANILLA TRIPLE","mt_inter-exter");
    FINSI
FINSI


     
