   ESTABLECEOPCION("RO_AGUJA","Ag15");
FINSI
SI[OPCION("RO_AGUJA","Ag15")=0]ENTONCES
   ESTABLECEOPCION("RO_AGUJA","Ag15");
FINSI
% Bombillo %
SI[OPCION("RO_BOMBILLO","Oculto") ]ENTONCES
   ESTABLECEOPCION("RO_BOMBILLO","b_sin bombillo");
FINSI
% Peso Corredera %
SI[OPCION("RO_COR_PESO","Oculto") ]ENTONCES
   ESTABLECEOPCION("RO_COR_PESO","Hasta 51Kg");
FINSI
% Manillas y tiradores %
SI[OPCION("RO_COR_MANILLA EXTERIOR","Oculto") ]ENTONCES
   ESTABLECEOPCION("RO_COR_MANILLA EXTERIOR","Cor. Manilla ext. Ninguna");
FINSI
SI[OPCION("RO_COR_MANILLA INTERIOR","Oculto") ]ENTONCES
   ESTABLECEOPCION("RO_COR_MANILLA INTERIOR","Cor. Tirador corto");
FINSI
SI[OPCION("RO_COR_MANILLA","Oculto") ]ENTONCES
   ESTABLECEOPCION("RO_COR_MANILLA","sin manilla triple");
FINSI
SI[OPCION("RO_COR_TIRADOR CORREDERA","Oculto") ]ENTONCES
   ESTABLECEOPCION("RO_COR_TIRADOR CORREDERA","tirador_sin tirador corredera");
FINSI


% 3. MECANIZADOS (Funcionan por Elemento)

SI [OPCION("RO_0MEC","Por Opcion")] ENTONCES

    % Bombillo %
    SI[OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. bombillo")]ENTONCES
        SI[OPCION("RO_COR_MANILLA INTERIOR","Cor. Tirador bombillo")]ENTONCES
            ESTABLECEOPCION("RO_BOMBILLO","b_inter-exter");
        FINSI
    FINSI
    SI[OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. bombillo")]ENTONCES
        SI[OPCION("RO_COR_MANILLA INTERIOR","Cor. Tirador bombillo")=0]ENTONCES
            ESTABLECEOPCION("RO_BOMBILLO","b_exterior");
        FINSI
    FINSI
    SI[OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. bombillo")=0]ENTONCES
        SI[OPCION("RO_COR_MANILLA INTERIOR","Cor. Tirador bombillo")]ENTONCES
            ESTABLECEOPCION("RO_BOMBILLO","b_interior");
        FINSI
    FINSI
    % Mecanizados manilla y tirador %
    SI[OPCION("RO_COR_MANILLA EXTERIOR","Cor. Manilla ext. Ninguna")]ENTONCES
       SI[OPCION("RO_COR_MANILLA INTERIOR","Cor. Rotoline") O
          OPCION("RO_COR_MANILLA INTERIOR","Cor. Rotoline secustik") O 
          OPCION("RO_COR_MANILLA INTERIOR","Cor. Rotoline llave") O 
          OPCION("RO_COR_MANILLA INTERIOR","Cor. Rotoswing") O
          OPCION("RO_COR_MANILLA INTERIOR","Cor. Rotoswing llave")]ENTONCES
          ESTABLECEOPCION("RO_COR_MANILLA","man_tri_interior");
          ESTABLECEOPCION("RO_COR_TIRADOR CORREDERA","tirador_sin tirador corredera");
       FINSI
       SI[OPCION("RO_COR_MANILLA INTERIOR","Cor. Tirador corto") O
          OPCION("RO_COR_MANILLA INTERIOR","Cor. Tirador largo") O 
          OPCION("RO_COR_MANILLA INTERIOR","Cor. Tirador bombillo")]ENTONCES
          ESTABLECEOPCION("RO_COR_MANILLA","sin manilla triple"); 
          ESTABLECEOPCION("RO_COR_TIRADOR CORREDERA","tirador_interior");
       FINSI
       SI[OPCION("RO_COR_MANILLA INTERIOR","Cor. Manilla uñero")]ENTONCES
          SI[OPCION("RO_BOMBILLO","b_sin bombillo") ]ENTONCES
             ESTABLECEOPCION("RO_COR_MANILLA","uñero_sin_bombillo"); 
          FINSI
          SI[OPCION("RO_BOMBILLO","b_interior") O OPCION("RO_BOMBILLO","b_exterior") O OPCION("RO_BOMBILLO","b_inter-exter")]ENTONCES
             ESTABLECEOPCION("RO_COR_MANILLA","uñero_con_bombillo"); 
          FINSI         
          ESTABLECEOPCION("RO_COR_TIRADOR CORREDERA","tirador_sin tirador corredera");
       FINSI
       SI[OPCION("RO_COR_MANILLA INTERIOR","Cor. Manilla Ninguna")]ENTONCES
          ESTABLECEOPCION("RO_COR_MANILLA","sin manilla triple");
          ESTABLECEOPCION("RO_COR_TIRADOR CORREDERA","tirador_sin tirador corredera");
       FINSI
    FINSI
    SI[OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. uñero") O 
       OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. corto") O 
       OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. largo") O 
       OPCION("RO_COR_MANILLA EXTERIOR","Cor. Tirador ext. bombillo")]ENTONCES
       SI[OPCION("RO_COR_MANILLA INTERIOR","Cor. Rotoline") O
          OPCION("RO_COR_MANILLA INTERIOR","Cor. Rotoline secustik") O 
          OPCION("RO_COR_MANILLA INTERIOR","Cor. Rotoline llave") O
          OPCION("RO_COR_MANILLA INTERIOR","Cor. Rotoswing") O 
          OPCION("RO_COR_MANILLA INTERIOR","Cor. Rotoswing llave")]ENTONCES
          ESTABLECEOPCION("RO_COR_MANILLA","man_tri_interior");
          ESTABLECEOPCION("RO_COR_TIRADOR CORREDERA","tirador_exterior");
       FINSI
       SI[OPCION("RO_COR_MANILLA INTERIOR","Cor. Tirador corto") O
          OPCION("RO_COR_MANILLA INTERIOR","Cor. Tirador largo") O 
          OPCION("RO_COR_MANILLA INTERIOR","Cor. Tirador bombillo")]ENTONCES
          ESTABLECEOPCION("RO_COR_MANILLA","sin manilla triple"); 
          ESTABLECEOPCION("RO_COR_TIRADOR CORREDERA","tirador_inter-exter");
       FINSI
       SI[OPCION("RO_COR_MANILLA INTERIOR","Cor. Manilla Ninguna")]ENTONCES
          ESTABLECEOPCION("RO_COR_MANILLA","sin manilla triple");
          ESTABLECEOPCION("RO_COR_TIRADOR CORREDERA","tirador_exterior");
       FINSI
    FINSI


