% ESTABLECE OPCIONES VISIBLES POR DEFECTO %


% 1. BASICAS CLIENTE

% Manilla %
SI[OPCION("RO_ELV_MANILLA","Oculto")]ENTONCES
    ESTABLECEOPCION("RO_ELV_MANILLA","Manilla+Uñero");
FINSI

% Dlo %

SI[OPCION("RO_ELV_DLO","Oculto")]ENTONCES
    ESTABLECEOPCION("RO_ELV_DLO","DLO_no");
FINSI


% 2. RESTO

% Cota Variable %
ESTABLECEOPCION("CotaVariable","No");
% Peso Elevable %
SI[OPCION("RO_ELV_PESO","Oculto")O OPCION("RO_ELV_PESO","200Kg")]ENTONCES
    ESTABLECEOPCION("RO_ELV_PESO","300Kg");
FINSI
% Manilla
SI[OPCION("RO_NT_MANILLA TRIPLE","Oculto")]ENTONCES
    ESTABLECEOPCION("RO_NT_MANILLA TRIPLE","mt_interior");
FINSI  
% Uñero exterior %
SI[OPCION("RO_M_UÑERO_EXTERIOR","Oculto")]ENTONCES
    ESTABLECEOPCION("RO_M_UÑERO_EXTERIOR","m_uñero_ext");
FINSI  


% 3. MECANIZADOS 

SI [OPCION("RO_0MEC","Por Opcion")] ENTONCES

    % Bombillo %
    SI[OPCION("RO_BOMBILLO","Oculto")]ENTONCES
        ESTABLECEOPCION("RO_BOMBILLO","b_sin bombillo");
    FINSI
    SI[OPCION("RO_ELV_MANILLA","Manilla cilindro+Uñero")=0 Y OPCION("RO_ELV_MANILLA","Manilla cilindro 2 caras")=0]ENTONCES
        ESTABLECEOPCION("RO_BOMBILLO","b_sin bombillo");
    FINSI
    SI[OPCION("RO_ELV_MANILLA","Manilla cilindro+Uñero") O OPCION("RO_ELV_MANILLA","Manilla cilindro 2 caras")]ENTONCES
        ESTABLECEOPCION("RO_BOMBILLO","b_inter-exter");
    FINSI

    % Mecanizado Manilla según pieza %    
    SI[OPCION("RO_ELV_MANILLA","Manilla+Uñero") O OPCION("RO_ELV_MANILLA","Manilla cilindro+Uñero")]ENTONCES
        ESTABLECEOPCION("RO_NT_MANILLA TRIPLE","mt_interior");
        ESTABLECEOPCION("RO_M_UÑERO_EXTERIOR","m_uñero_ext");
    FINSI
    SI[OPCION("RO_ELV_MANILLA","Manilla cilindro 2 caras")]ENTONCES
        ESTABLECEOPCION("RO_NT_MANILLA TRIPLE","mt_inter-exter");
        ESTABLECEOPCION("RO_M_UÑERO_EXTERIOR","m_sin_uñero_ext");
    FINSI
    SI[OPCION("RO_ELV_MANILLA","Manilla Elev. Ninguna") O OPCION("RO_ELV_MANILLA","Oculto")]ENTONCES
        ESTABLECEOPCION("RO_NT_MANILLA TRIPLE","mt_sin manilla triple");
        ESTABLECEOPCION("RO_M_UÑERO_EXTERIOR","m_sin_uñero_ext");
    FINSI


FINSI
