% ESTABLECE OPCIONES VISIBLES POR DEFECTO %


% 1. BASICAS CLIENTE

% Tipo Osciloparalela %
SI[OPCION("RO_ALV_TIPO","Oculto")]ENTONCES
   ESTABLECEOPCION("RO_ALV_TIPO","KS-100");
FINSI
% Cremona %
SI[OPCION("RO_ALV_CREMONA","Oculto")]ENTONCES
   ESTABLECEOPCION("RO_ALV_CREMONA","Cremona simple");
FINSI
% Aguja %
SI[OPCION("RO_AGUJA","Ag15")=0 Y OPCION("RO_AGUJA","Ag30")=0]ENTONCES
   ESTABLECEOPCION("RO_AGUJA","Ag15");
FINSI


% 2. RESTO
% Control Mecanizados %
SI [OPCION("RO_0MEC","Oculto")] ENTONCES
    ESTABLECEOPCION("RO_0MEC","Por Opcion");
FINSI
% Cota Variable %
ESTABLECEOPCION("CotaVariable","Sí");
% Falsa Maniobra %
ESTABLECEOPCION("RO_NT_FALSA MANIOBRA","NT_FALSA MANIOBRA_no");
% Esquema Osciloparalela %
SI[OPCION("AsociadaOpuesta","Ninguna")]ENTONCES                % ESQUEMA A 
   ESTABLECEOPCION("RO_ALV_ESQ.C","Oculto");
FINSI 
SI[OPCION("AsociadaOpuesta","Oscilobatiente")]ENTONCES         % ESQUEMA C 
    SI[OPCION("RO_ALV_ESQ.C","Oculto")]ENTONCES
      ESTABLECEOPCION("RO_ALV_ESQ.C","Esq. C Derecha");
    FINSI
FINSI 
% Tipo Osciloparalela %
SI[OPCION("RO_ALV_TIPO","KS-100") O OPCION("RO_ALV_TIPO","KS-160")]ENTONCES 
    ESTABLECEOPCION("RO_ALV_PS_VENTILACION","Oculto"); 
FINSI   
SI[OPCION("RO_ALV_TIPO","PS-160") O OPCION("RO_ALV_TIPO","PS-200")]ENTONCES
    SI[OPCION("RO_ALV_PS_VENTILACION","Oculto")]ENTONCES
        ESTABLECEOPCION("RO_ALV_PS_VENTILACION","Sin ventilacion");
    FINSI
FINSI  
SI[OPCION("RO_ALV_TIPO","PS Air-160") O OPCION("RO_ALV_TIPO","PS AirCom-160") O OPCION("RO_ALV_TIPO","PS AirCom-200") ]ENTONCES
    ESTABLECEOPCION("RO_ALV_PS_VENTILACION","Oculto"); 
FINSI


% 3. MECANIZADOS 

SI [OPCION("RO_0MEC","Por Opcion")] ENTONCES

    % Bombillo y manilla %
    SI[OPCION("RO_ALV_CREMONA","Cremona simple")]ENTONCES
        ESTABLECEOPCION("RO_BOMBILLO","Oculto");
        ESTABLECEOPCION("RO_NT_MANILLA TRIPLE","mt_interior");
    FINSI
    SI[OPCION("RO_ALV_CREMONA","Cremona con bombillo") ]ENTONCES
       SI[(OPCION("RO_BOMBILLO","b_inter-exter") O OPCION("RO_BOMBILLO","b_interior"))=0]ENTONCES
           ESTABLECEOPCION("RO_BOMBILLO","b_inter-exter");
       FINSI
       %SI[(OPCION("RO_NT_MANILLA TRIPLE","mt_inter-exter") O OPCION("RO_NT_MANILLA TRIPLE","mt_interior"))=0]ENTONCES
           ESTABLECEOPCION("RO_NT_MANILLA TRIPLE","mt_inter-exter");
       %FINSI
    FINSI

FINSI

SI [OPCION("RO_0MEC","Libre")] ENTONCES
    SI[OPCION("RO_NT_MANILLA TRIPLE","Oculto")]ENTONCES
        SI[OPCION("RO_ALV_CREMONA","Cremona simple") ]ENTONCES
            ESTABLECEOPCION("RO_NT_MANILLA TRIPLE","mt_interior");
        FINSI
        SI[OPCION("RO_ALV_CREMONA","Cremona con bombillo") ]ENTONCES
            ESTABLECEOPCION("RO_NT_MANILLA TRIPLE","mt_inter-exter");
        FINSI
    FINSI
FINSI
