% ESTABLECE OPCIONES VISIBLES POR DEFECTO %

% 1. BASICAS CLIENTE

% Manilla %
SI[OPCION("RO_MANILLA VENTANA","Oculto")]ENTONCES
   ESTABLECEOPCION("RO_MANILLA VENTANA","Rotoline");
FINSI
% Tipo cerradero %
SI[OPCION("RO_NT_AL_TIPO CERRADERO","Oculto")]ENTONCES
    ESTABLECEOPCION("RO_NT_AL_TIPO CERRADERO","Con apriete");
FINSI

SI[OPCION("RO_NX_SOPORTE COMPAS P","Oculto")]ENTONCES
   ESTABLECEOPCION("RO_NX_SOPORTE COMPAS P","3/130");
FINSI


% 2. RESTO

% Aguja %
SI[OPCION("RO_AGUJA","Ag8")=0 Y OPCION("RO_AGUJA","Ag15")=0]ENTONCES
   ESTABLECEOPCION("RO_AGUJA","Ag15");
FINSI
% Balconera %
SI[OPCION("RO_NX_EASY MIX","Oculto")]ENTONCES                                  
   ESTABLECEOPCION("RO_NX_EASY MIX","Easy Mix_No");                                       
FINSI
% Falsa maniobra %
SI[OPCION("RO_NT_FALSA MANIOBRA","Oculto")]ENTONCES                                  
   ESTABLECEOPCION("RO_NT_FALSA MANIOBRA","NT_FALSA MANIOBRA_si");                                       
FINSI
% Tipo Herraje %
SI[OPCION("RO_NX_TIPO HERRAJE","Oculto")]ENTONCES
   ESTABLECEOPCION("RO_NX_TIPO HERRAJE","Normal");
FINSI
% Herraje Seguridad %
SI [OPCION("RO_NT_AL_SEGURIDAD","Oculto")]ENTONCES 
    ESTABLECEOPCION("RO_NT_AL_SEGURIDAD","AL_STD");                                     
FINSI
% Peso Designo %
SI[OPCION("RO_NX_TIPO HERRAJE","Designo") Y OPCION("RO_PESO_DESIGNO","Oculto")]ENTONCES
   ESTABLECEOPCION("RO_PESO_DESIGNO","Hasta 100kg (sin dispositivo)");                     
FINSI
SI[OPCION("RO_NX_TIPO HERRAJE","Designo")=0]ENTONCES
   ESTABLECEOPCION("RO_PESO_DESIGNO","Oculto");
FINSI
% Cota Variable %
SI[OPCION("CotaVariable","Sí")]ENTONCES
    ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","Oculto");    
FINSI
% Hoja pasiva %
SI[OPCION("RO_NT_AL_HOJA PASIVA","Oculto")] ENTONCES
    ESTABLECEOPCION("RO_NT_AL_HOJA PASIVA","AL_Cremona");
FINSI

% Herraje Activa %
SI [OPCION("Activa","Sí")]ENTONCES
    %1H 
    SI [OPCION("Asociada","Ninguna")]ENTONCES   
        ESTABLECEOPCION("RO_NT_AL_HOJA PASIVA","Oculto");                              
        ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","Oculto");                             
    FINSI
    %2H
    SI [OPCION("Asociada","Practicable")]ENTONCES
        SI [OPCION("RO_NT_AL_HOJA PASIVA","Oculto")]ENTONCES   
            ESTABLECEOPCION("RO_NT_AL_HOJA PASIVA","AL_Cremona");                    
        FINSI
        SI [OPCION("RO_NX_ALTURA MAN. PASIVA","Oculto") Y OPCION("CotaVariable","No")]ENTONCES
            ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","Por defecto");            
        FINSI
    FINSI
FINSI


% 3. MECANIZADOS

% Mecanizado Manilla %
SI [OPCION("RO_0MEC","Por Opcion")] ENTONCES
    % La triple manill se mecaniza siempre
FINSI

% ALTURA DE MANILLA %
SI [OPCION ("CotaVariable", "No") Y OPCION("Asociada","Practicable")]ENTONCES
    SI [OPCION("AlturaManetaPorDefecto","Sí")] ENTONCES
        ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","Por defecto");
    FINSI
    SI [OPCION("AlturaManetaPorDefecto","Sí")=0] ENTONCES
        SI [OPCION("AlturaManeta","120.00")] ENTONCES
            ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","140");
        FINSI
        SI [OPCION("AlturaManeta","170.00")] ENTONCES
            ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","190");
        FINSI
        SI [OPCION("AlturaManeta","263.00")] ENTONCES
            ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","283");
        FINSI
        SI [OPCION("AlturaManeta","413.00")] ENTONCES
            ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","433");
        FINSI
        SI [OPCION("AlturaManeta","513.00")] ENTONCES
            ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","533");
        FINSI
        SI [OPCION("AlturaManeta","563.00")] ENTONCES
            ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","583");
        FINSI
        SI [OPCION("AlturaManeta","1000.00")] ENTONCES
            ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","1020");
        FINSI
    FINSI
FINSI


% GESTIÓN DE ERRORES %

% Practicable Seguridad STD siempre %
SI [OPCION("RO_NT_AL_SEGURIDAD","AL_STD")=0]ENTONCES 
    ESTABLECEOPCION("RO_NT_AL_SEGURIDAD","AL_STD");                                     
FINSI

