   ESTABLECEOPCION("RO_NX_CLIC RETENCION","Clic retencion_si");
FINSI
% Embellecedores %
SI[OPCION("RO_NX_EMBELLECEDORES","Oculto")]ENTONCES
   ESTABLECEOPCION("RO_NX_EMBELLECEDORES","Por Piezas");
FINSI


% 2. RESTO

% Aguja %
SI[OPCION("RO_AGUJA","Ag15")=0]ENTONCES
   ESTABLECEOPCION("RO_AGUJA","Ag15");
FINSI
% Balconera %
SI[OPCION("RO_NX_EASY MIX","Oculto")]ENTONCES                                  
   ESTABLECEOPCION("RO_NX_EASY MIX","Easy Mix_No");                                       
FINSI
% Soporte Compás P %
SI[OPCION("RO_NX_SOPORTE COMPAS P","Oculto")]ENTONCES
   ESTABLECEOPCION("RO_NX_SOPORTE COMPAS P","6/130");                                     
FINSI
% Tipo Herraje %
SI[OPCION("RO_NX_TIPO HERRAJE","Oculto")]ENTONCES
   ESTABLECEOPCION("RO_NX_TIPO HERRAJE","Normal");
FINSI
% Peso Designo %
SI[OPCION("RO_NX_TIPO HERRAJE","Designo") Y OPCION("RO_PESO_DESIGNO","Oculto")]ENTONCES
   ESTABLECEOPCION("RO_PESO_DESIGNO","Hasta 100kg (sin dispositivo)");
   ESTABLECEOPCION("RO_NX_SOPORTE COMPAS P","Oculto");                     
FINSI
SI[OPCION("RO_NX_TIPO HERRAJE","Designo")=0]ENTONCES
   ESTABLECEOPCION("RO_PESO_DESIGNO","Oculto");
FINSI
% Cota Variable %
SI[OPCION("CotaVariable","Sí")]ENTONCES
    ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","Oculto");    
FINSI
% Herraje Activa %
SI [OPCION("Activa","Sí")]ENTONCES
    %1H 
    SI [OPCION("Asociada","Ninguna")]ENTONCES   
        ESTABLECEOPCION("RO_NX_HERR. HOJA PASIVA","Oculto");                              
        ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","Oculto");
        ESTABLECEOPCION("RO_NX_EMBELLECEDORES","Oculto");
    FINSI
    %2H
    SI [OPCION("Asociada","Practicable")]ENTONCES
        SI [OPCION("RO_NX_HERR. HOJA PASIVA","Oculto")]ENTONCES   
            ESTABLECEOPCION("RO_NX_HERR. HOJA PASIVA","Perimetral");                    
        FINSI
        SI [OPCION("RO_NX_ALTURA MAN. PASIVA","Oculto") Y OPCION("CotaVariable","No")]ENTONCES
            ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","Por defecto");            
        FINSI
    FINSI
FINSI

% Establecer altura da mailla pasiva en cota fija %
SI[OPCION("CotaVariable","No")]ENTONCES
    SI [OPCION("AlturaManetaPorDefecto","Sí")]ENTONCES
        ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","Por defecto");
    FINSI
    SI [OPCION("AlturaManetaPorDefecto","No")]ENTONCES
        SI [AM=140] ENTONCES  
            ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","140");
        FINSI
        SI [AM=190] ENTONCES  
            ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","190");
        FINSI
        SI [AM=283] ENTONCES  
            ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","283");
        FINSI
        SI [AM=433] ENTONCES  
            ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","433");
        FINSI
        SI [AM=533] ENTONCES  
            ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","533");
        FINSI
        SI [AM=583] ENTONCES  
            ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","583");
        FINSI
        SI [AM=1020] ENTONCES  
            ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","Por defecto");
        FINSI
        SI [AM=240] ENTONCES  
            ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","E 240");
        FINSI
        SI [AM=783] ENTONCES  
            ESTABLECEOPCION("RO_NX_ALTURA MAN. PASIVA","E 783");
        FINSI
    FINSI



% 3. MECANIZADOS

