% Determinar OPCIONES POR DEFECTO en CLIENTE

SI[OPCION("HardwareSupplier","ROTO NX")]ENTONCES
    
    %------------------------------------
    SI[OPCION("01 Configuracion Estandar","Si")]ENTONCES
    
        
        % Uñero exterior %
        ESTABLECEOPCION("RO_M_UÑERO_EXTERIOR","m_sin_uñero_ext");
    
        % Cremona %
        %ESTABLECEOPCION("RO_COR_CREMONA","crem. simple");
    
        % Manilla interior %
        %ESTABLECEOPCION("RO_COR_MANILLA INTERIOR","Cor. Rotoline");
    
        %ESTABLECEOPCION("RO_COR_MANILLA","man_tri_interior");
    
    
        % Manilla exterior %
        %ESTABLECEOPCION("RO_COR_MANILLA EXTERIOR","Cor. Manilla ext. Ninguna");
    
        % Tirador %
        %ESTABLECEOPCION("RO_COR_TIRADOR CORREDERA","tirador_sin tirador corredera");
        
    
    FINSI
    
    SI[OPCION("01 Configuracion Estandar","Si")=0]ENTONCES
    
    FINSI
FINSI

