SI[OPCION("HardwareSupplier","ROTO NX")]ENTONCES 
    %------------------------------------
    SI[OPCION("01 Configuracion Estandar","Si")]ENTONCES
    
        % Aguja %
        SI[OPCION("01 SISTEMA","EUROFUTUR") O OPCION("01 SISTEMA","EURODUR")] ENTONCES
            ESTABLECEOPCION("RO_AGUJA","Ag35");
        FINSI
        
        SI[OPCION("01 SISTEMA","SISTEMA 76") O OPCION("01 SISTEMA","SISTEMA 76MD") O OPCION("01 SISTEMA","SISTEMA 76AD")] ENTONCES
           ESTABLECEOPCION("RO_AGUJA","Ag40");
        FINSI
        
        % Bombillo %
        ESTABLECEOPCION("RO_BOMBILLO","b_inter-exter");
        
        % Manilla % 
        ESTABLECEOPCION("RO_MANILLA PUERTA","Solo Manilla interior sin placa");
        
        % Mecanizado Manilla %    
        
        SI[OPCION("RO_MANILLA PUERTA","Juego Manilla dos caras") O OPCION("RO_MANILLA PUERTA","Manilla + Tirador")]ENTONCES
            ESTABLECEOPCION("RO_PU_MANILLA CERRADURA","mc_inter-exter"); 
            ESTABLECEOPCION("RO_PU_PLACA MAN CERRADURA","pm_inter-exter");
        FINSI
        SI[OPCION("RO_MANILLA PUERTA","Manilla + Placa ciega")]ENTONCES
            ESTABLECEOPCION("RO_PU_MANILLA CERRADURA","mc_interior"); 
            ESTABLECEOPCION("RO_PU_PLACA MAN CERRADURA","pm_interior");
        FINSI
        SI[OPCION("RO_MANILLA PUERTA","Manilla puerta Ninguna")]ENTONCES
            ESTABLECEOPCION("RO_PU_MANILLA CERRADURA","mc_sin manilla"); 
            ESTABLECEOPCION("RO_PU_PLACA MAN CERRADURA","pm_sin placa");
        FINSI
        SI[OPCION("RO_MANILLA PUERTA","Solo Manilla interior sin placa")]ENTONCES
            ESTABLECEOPCION("RO_PU_MANILLA CERRADURA","mc_interior"); 
            ESTABLECEOPCION("RO_PU_PLACA MAN CERRADURA","pm_sin placa");
        FINSI
        SI[OPCION("RO_MANILLA PUERTA","Manilla dos caras sin placa")]ENTONCES
            ESTABLECEOPCION("RO_PU_MANILLA CERRADURA","mc_inter-exter"); 
            ESTABLECEOPCION("RO_PU_PLACA MAN CERRADURA","pm_sin placa");
        FINSI
        
        % Bisagra superior %
        ESTABLECEOPCION("RO_PU_BIS SUPERIOR PUERTA","contraventana_no");
        
        % Portero electrico %
        ESTABLECEOPCION("RO_PU_PORTERO ELECTRICO","no");
        
        FINSI
    
    SI[OPCION("01 Configuracion Estandar","Si")=0]ENTONCES
    
    FINSI
FINSI


