%MANILLA VENTANA/BALCONERA/PUERTA
%--------------------------------
%Act. 20150331 - Samba
%Act. 20170502 - Abatibles
%Act. 20181009 - Opcion check Puerta
%--------
SI [OPCION("Activa","Sí")] ENTONCES
    SI [OPCION("Puerta","No")] ENTONCES
        SI [OPCION("Practicable","Ninguna")=0] ENTONCES
            %Ventanas 
            SI [OPCION("RO_NX_EASY MIX","Easy Mix_No") OR OPCION("RO_NT_CERRADURA BALCONERA","No")] ENTONCES
                SI [OPCION("RO_MANILLA VENTANA","Rotoline")] ENTONCES
                    MENSAJE("Manilla Ventana Rotoline");
                    PIEZAC("RO_256756",[1]);
                FINSI
                SI [OPCION("RO_MANILLA VENTANA","Rotoline secustik")] ENTONCES
                    MENSAJE("Manilla Ventana Rotoline secustik");
                    PIEZAC("RO_487657",[1]);
                FINSI
                SI [OPCION("RO_MANILLA VENTANA","Rotoline llave")] ENTONCES
                    MENSAJE("Manilla Ventana Rotoline llave");
                    PIEZAC("RO_228044",[1]);
                FINSI
                SI [OPCION("RO_MANILLA VENTANA","Rotoswing")] ENTONCES
                    MENSAJE("Manilla Ventana Rotoswing");
                    PIEZAC("RO_602370",[1]);
                FINSI
                SI [OPCION("RO_MANILLA VENTANA","Rotoswing secustik")] ENTONCES
                    MENSAJE("Manilla Ventana Rotoswing secustik");
                    PIEZAC("RO_602389",[1]);
                FINSI
                SI [OPCION("RO_MANILLA VENTANA","Rotoswing llave")] ENTONCES
                    MENSAJE("Manilla Ventana Rotoswing llave");
                    PIEZAC("RO_602375",[1]);
                FINSI
                SI [OPCION("RO_MANILLA VENTANA","Rotosamba")] ENTONCES
                    MENSAJE("Manilla Ventana Roto Samba");
                    PIEZAC("RO_639623",[1]);
                FINSI
                SI [OPCION("RO_MANILLA VENTANA","Rotosamba secustik")] ENTONCES
                    MENSAJE("Manilla Ventana Roto Samba secustik");
                    PIEZAC("RO_639699",[1]);
                FINSI
                SI [OPCION("RO_MANILLA VENTANA","Rotosamba llave")] ENTONCES
                    MENSAJE("Manilla Ventana Roto Samba llave");
                    PIEZAC("RO_639719",[1]);
                FINSI
                SI [OPCION("RO_MANILLA VENTANA","Manilla Quita y Pon")] ENTONCES
                    MENSAJE("Manilla Quita y Pon");
                    PIEZAC("RO_228014",[1]);
                    PIEZAC("RO_257628",[1]);
                FINSI

                SI [OPCION("RO_MANILLA VENTANA","Rotoline llave apertura logica")] ENTONCES
                    MENSAJE("Manilla  Rotoline llave apertura logica");
                    PIEZAC("RO_618815",[1]);
                FINSI

            FINSI
        
            SI [OPCION("RO_NX_EASY MIX","Easy Mix_Si") OR OPCION("RO_NT_CERRADURA BALCONERA","Si")] ENTONCES
                %Balconeras
                SI [OPCION("RO_SEC_TIPO BALCONERA","Balconera")] ENTONCES
                    SI [OPCION("RO_MANILLA BALCONERA","Manilla + Manilla")] ENTONCES
                        MENSAJE ("Manilla+manilla Balconera Roto");
                        PIEZAC("RO_228337",[1]);
                    FINSI
                    SI [OPCION("RO_MANILLA BALCONERA","Manilla + Manilla plana")] ENTONCES
                        MENSAJE ("Manilla+manilla plana Balconera Roto");
                        PIEZAC("RO_228349",[1]);
                    FINSI
                    SI [OPCION("RO_MANILLA BALCONERA","Solo manilla interior")] ENTONCES
                        MENSAJE ("Manilla Balconera Rotoline");
                        PIEZAC("RO_487657",[1]); %Mejor aplicar el valor seleccionado en la opcoión MANILLA VENTANA
                    FINSI
                FINSI
                %Puertas secundarias
                SI [OPCION("RO_SEC_TIPO BALCONERA","Balconera")=0] ENTONCES            
                    SI [OPCION("RO_MANILLA PUERTA","Juego Manilla dos caras")] ENTONCES
                        MENSAJE ("Juego manillas Puerta Roto");
                        PIEZAC("RO_319954",[1]);
                    FINSI
                    SI [OPCION("RO_MANILLA PUERTA","Manilla + Tirador")] ENTONCES
                        MENSAJE ("Manilla+tirador Puerta Roto");
                        PIEZAC("RO_340484",[1]);
                    FINSI
                    SI [OPCION("RO_MANILLA PUERTA","Manilla + Placa ciega")] ENTONCES
                        MENSAJE ("Manilla+placa Puerta Roto");
                        PIEZAC("RO_565418",[1]);
                    FINSI
                    SI [OPCION("RO_MANILLA PUERTA","Manilla + Manilla Plana")] ENTONCES
                        MENSAJE ("Manilla+Manilla plana exterior");
                        PIEZAC("RO_337617",[1]);
                    FINSI 
                FINSI
            FINSI
        FINSI
        %Abatibles
        SI [OPCION("Practicable","Ninguna") Y OPCION ("Oscilobatiente","Inferior") Y OPCION("Corredera","Ninguna")] ENTONCES
            SI [OPCION("RO_MANILLA VENTANA","Rotoline")] ENTONCES
                MENSAJE("Manilla Ventana Rotoline Abatible");
                PIEZAC("RO_256756",[1]);
            FINSI
            SI [OPCION("RO_MANILLA VENTANA","Rotoline secustik")] ENTONCES
                MENSAJE("Manilla Ventana Rotoline secustik Abatible");
                PIEZAC("RO_487657",[1]);
            FINSI
            SI [OPCION("RO_MANILLA VENTANA","Rotoline llave")] ENTONCES
                MENSAJE("Manilla Ventana Rotoline llave Abatible");
                PIEZAC("RO_228044",[1]);
            FINSI
            SI [OPCION("RO_MANILLA VENTANA","Rotoswing")] ENTONCES
                MENSAJE("Manilla Ventana Rotoswing Abatible");
                PIEZAC("RO_602370",[1]);
            FINSI
            SI [OPCION("RO_MANILLA VENTANA","Rotoswing secustik")] ENTONCES
                MENSAJE("Manilla Ventana Rotoswing secustik Abatible");
                PIEZAC("RO_602389",[1]);
            FINSI
            SI [OPCION("RO_MANILLA VENTANA","Rotoswing llave")] ENTONCES
                MENSAJE("Manilla Ventana Rotoswing llave Abatible");
                PIEZAC("RO_602375",[1]);
            FINSI
            SI [OPCION("RO_MANILLA VENTANA","Rotosamba")] ENTONCES
                MENSAJE("Manilla Ventana Roto Samba Abatible");
                PIEZAC("RO_639623",[1]);
            FINSI
            SI [OPCION("RO_MANILLA VENTANA","Rotosamba secustik")] ENTONCES
                MENSAJE("Manilla Ventana Roto Samba secustik Abatible");
                PIEZAC("RO_639699",[1]);
            FINSI
            SI [OPCION("RO_MANILLA VENTANA","Rotosamba llave")] ENTONCES
                MENSAJE("Manilla Ventana Roto Samba llave Abatible");
                PIEZAC("RO_639719",[1]);
            FINSI
        FINSI
    FINSI
    
    %Puertas
    SI [OPCION("Puerta","Sí")] ENTONCES
        SI [OPCION("RO_MANILLA PUERTA","Juego Manilla dos caras")] ENTONCES
            MENSAJE ("Juego manillas Puerta Roto");
            PIEZAC("RO_319954",[1]);
        FINSI
        SI [OPCION("RO_MANILLA PUERTA","Manilla + Tirador")] ENTONCES
            MENSAJE ("Manilla+tirador Puerta Roto");
            PIEZAC("RO_340484",[1]);
        FINSI
        SI [OPCION("RO_MANILLA PUERTA","Manilla + Placa ciega")] ENTONCES
            MENSAJE ("Manilla+placa Puerta Roto");
            PIEZAC("RO_565418",[1]);
        FINSI
        SI [OPCION("RO_MANILLA PUERTA","Manilla + Manilla Plana")] ENTONCES
            MENSAJE ("Manilla+Manilla plana exterior");
            PIEZAC("RO_337617",[1]);
        FINSI
    FINSI
FINSI
